mapper.select:
SELECT SCH_NO, TITLE, BEGIN AS BEGIN_DATE, END AS END_DATE, COLOR, NOTE, LOCATION
  FROM TB_INDIVIDUAL
 WHERE SCH_NO = #{schNo}

mapper.update:
UPDATE TB_INDIVIDUAL
   SET TITLE=#{title}, BEGIN=#{beginDate}, END=#{endDate}, LOCATION=#{location}, NOTE=#{note}, COLOR=#{color}
 WHERE SCH_NO=#{schNo}

UPDATE TB_INDIVIDUAL
   SET DELETED = 'Y'
 WHERE SCH_NO=#{schNo}
